import MusiaLogo from '../assets/logo_MusIA.png';
import Listening from './Listening';
import '../styles/Home.css';
import { useEffect, useState } from 'react';


function Home({data,setdata,isDisplay,setIsDisplay,id,setid,setIsContent,isListening,setIsListening,isReceiving,setIsReceiving}) {
  
  const [FinishL, setIsFinishL] = useState(false)

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    setLoading(true);
    fetch("https://jsonplaceholder.typicode.com/todos/1")
      .then((response) => response.json())
      .then((data) => {
        setTodo(data);
        setLoading(false);
      })
      .catch((error) => {
        console.log(error);
        setLoading(false);
      });
  };
    useEffect(() => {
        if(isListening === true){
          var constraints = { audio: true }
          var chunks = []
          navigator.mediaDevices.getUserMedia(constraints)
          .then(function(stream) {
              const recorder = new MediaRecorder(stream);
              recorder.ondataavailable = event => {
                  chunks.push(event.data);
                  var blob = new Blob(chunks, {
                      type: "audio/mp3"
                  });
                  var xmlhttp = new XMLHttpRequest();
                  var url = " https://ee0c-185-144-25-51.eu.ngrok.io/api/v01/post/audio-blob/";
                  xmlhttp.open("POST", url, true);
                  xmlhttp.setRequestHeader("Content-type", "audio/wav");
                  xmlhttp.setRequestHeader("ngrok-skip-browser-warning","69420")
                  xmlhttp.onreadystatechange = function() {//Call a function when the state changes.
                  if(xmlhttp.readyState === 4 && xmlhttp.status === 200) {
                      setid(xmlhttp.responseText)
                      console.log(id)
                  }
                  }
                  xmlhttp.send(blob);
              };
              const sleep = time => new Promise(resolve => setTimeout(resolve, time));

              (async () => {
              if(isListening===true && FinishL===false){
                recorder.start(7000);
                await sleep(8000);
                await recorder.stop();
                setIsFinishL(true)
              }
          })();
          })
          .catch(function(err) {
              /* handle the error */
              setIsReceiving(false);
              setIsListening(false);
              setIsContent(0)
          });
          if(FinishL===true){
            setIsReceiving(true)
            
          }
      }
        
    },[isListening,FinishL,setIsListening,setIsReceiving,setIsFinishL,id,setid,setIsContent]);
  
  // LISTENING TO THE MICROPHONE
  return isListening || isReceiving ? (
    <Listening isDisplay={isDisplay} setIsDisplay={setIsDisplay} setdata={setdata} id={id} setIsContent={setIsContent} isReceiving={isReceiving} setIsReceiving={setIsReceiving} setIsListening={setIsListening}/>
  )  :  (
    <div className="Home">
        
        <button className="transbutton" alt="Stop Listening" onClick={() => setIsListening(true)}><img src={MusiaLogo} className="App-logo-2" alt="Logo" /></button>
        <p className='NameButton'>
          Cliquez sur l'icone pour démarrer l'écoute.
        </p>
        <button className="Button" alt="Change to URL" onClick={() => setIsContent(1)}>Test URL</button>
    </div>
  );
}
export default Home;
