import MusiaLogotransparent from '../assets/logo_trans.png'
import '../styles/Content.css';

import { useState, useEffect } from 'react'
import Prediction from './Prediction';

useEffect(() => {
  if (isReceiving === true && isDisplay === false) {
    fetch(
      `https://ee0c-185-144-25-51.eu.ngrok.io/api/v01/get/res/?id=${id.id}`,
      {
        method: "get",
        headers: new Headers({
          "ngrok-skip-browser-warning": "69420",
        }),
      }
    )
      .then((response) => {
        console.log(response);
        return response.json();
      })
      .then((result) => {
        console.log(result);
        if (result[1] === -1) {
          alert(
            "La musique n'a pas été reconnu essayons par URL"
          );
          setIsReceiving(false);
          setIsListening(false);
          setIsContent(1);
        } else {
          setdata(result);
          setIsDisplay(true);
        }
      })
      .catch((error) => {
        console.error(error);
        const errorMessage = "Something went wrong";
        console.log(errorMessage);
        setIsListening(0);
        setIsReceiving(0);
        setIsContent(0);
      });
  }
}, [isReceiving, isDisplay, data, setIsContent, setIsListening, setIsReceiving, id]);
