import menu from './assets/Menu.png';
import Banner from './Components/Banner.js'
import Return from './assets/Return.png'
import './styles/App.css';
import Content from './Components/Content.js';
import Footer from './Components/Footer.js';
import Menu from './Components/Menu';
import { useState } from 'react'

function App() {
  const [isMenu, setIsMenu] = useState(false)
  const [isContent, setIsContent] = useState(0)
  function switchMenu(){
    if(isMenu){
      setIsMenu(false)
    }
    else{
      setIsMenu(true)
    }
  }
  
  
  return isMenu ? (
    <div className="App">
      
        <Banner>
          <img src={menu} alt='logo-menu' className='mia-logo'  onClick={()=> switchMenu()} />
          <h1 className='mia-title'>MusIA</h1>
        </Banner>
        <Menu />
        <Footer />
      
    </div>
  ) : (
    <div className='App'>
      <Banner>
          <img src={Return} alt='menu' className='mia-logo' onClick={()=> setIsContent(0)} />
          <h1 className='mia-title'>MusIA</h1>
        </Banner>
      <Content isContent={isContent} setIsContent={setIsContent} />
      <Footer />
    </div>
  );
}

export default App;
