import MusiaPrediction from '../assets/MusIA_Predict.jpeg';
import '../styles/Display.css';
import BarChart from './BarChart';



function Prediction({isDisplay,setIsDisplay,data}) {

  const datagenre = data[1]  
  const name = data[0]['title']
  console.log(name)
  console.log(data)
  return isDisplay ? (
    <div className="Display">
        <p>
          MusIA a fini ces prédictions, voici le résultat :
        </p>
        <p>
          {name}
        </p>
        <BarChart data={datagenre} />

        
    </div>
  )  :  (
    <div className="Prediction">
        <img src={MusiaPrediction} className="App-logo" alt="Logo" />
        <p>
          MusIA effectue ces predictions ! Veuillez patienter un instant. 
        </p>
        
    </div>
  );
} 

export default Prediction;
