import MusiaLogotransparent from '../assets/logo_trans.png'
import '../styles/Content.css';

import { useState, useEffect } from 'react'
import Prediction from './Prediction';

function Listening({finishL,id,setIsContent,isReceiving,setIsReceiving,setIsListening}) {
  
  const [data, setdata] = useState({});
  const [isDisplay, setIsDisplay] = useState(false);

 
  useEffect(() => {
    if (isReceiving && !isDisplay && (id.lenghts > 16)) { // check if id is not undefined or null
      fetch(`https://ee0c-185-144-25-51.eu.ngrok.io/api/v01/get/res/?id=`+id, {
        method: "get",
        headers: new Headers({
          "ngrok-skip-browser-warning": "69420",
        }),
      })
      .then(response => {
        console.log(response)
        return response.json(); // <<- Return the JSON Object
      })
      .then(result => {
        console.log(result)
        if (result[1] === -1) {
          alert("La musique n'a pas été reconnue, essayons par URL");
          setIsReceiving(false);
          setIsListening(false);
          setIsContent(1);
        } else {
          setdata(result);
          setIsDisplay(true);
        }
      })
      .catch(error => { 
        console.error(error);
        const errorMessage = 'Something went wrong';
        console.log(errorMessage);
        setIsListening(0);
        setIsReceiving(0);
        setIsContent(0);
      });
    }
  }, [isReceiving, isDisplay, id, setIsContent, setIsListening, setIsReceiving]);
  
  
  return isReceiving ? (
    <Prediction isDisplay={isDisplay} setIsDisplay={setIsDisplay} data={data} />
  )  :  (
    <div className="ContentListening">
        <button className="transbutton" alt="Listening" ><img src={MusiaLogotransparent} className="App-logo" alt="Logo" /></button>
        <p>
          L'application est en écoute, veuillez patienter.
        </p>
    </div>
  );
} 

export default Listening;
