import MusiaLogotransparent from '../assets/logo_trans.png'
import '../styles/Content.css';

import { useState, useEffect } from 'react'


function Listening({isDisplay,setIsDisplay,id,setIsContent,isReceiving,setIsReceiving,setIsListening}) {
  
  

 
  useEffect(() => {
    if(isReceiving===true && isDisplay ===false){
      
      fetch(' https://ee0c-185-144-25-51.eu.ngrok.io/api/v01/get/res/?id='+id, {
        method: "get",
        headers: new Headers({
          "ngrok-skip-browser-warning": "69420",
        }),
      })
      .then(response => {
          console.log(response)
          return response.json(); // <<- Return the JSON Object
      })
      .then(result => {
        console.log(result)
        if(result[1] === -1)
        {
          alert("La musique n'a pas été reconnu essayons par URL")
          setIsReceiving(false)
          setIsListening(false)
          setIsContent(1)         
        }
        else{
          setdata(result);
          setIsDisplay(true);
          setIsContent(3)    
        }
        
      })
      .catch(error => { // Use .catch() to catch exceptions. Either in the request or any of your .then() blocks
          console.error(error); // Log the error object in the console.
          const errorMessage = 'Something went wrong';
          console.log(errorMessage);
          setIsListening(0);
          setIsReceiving(0);
          setIsContent(0);
      });
    }
},[isReceiving,isDisplay,data,setIsContent,setIsListening,setIsReceiving,id]);
 
  return (
    <div className="ContentListening">
        <button className="transbutton" alt="Listening" ><img src={MusiaLogotransparent} className="App-logo" alt="Logo" /></button>
        <p>
          L'application est en écoute, veuillez patienter.
        </p>
    </div>
  );
} 

export default Listening;
