import { useD3 } from '../hooks/useD3';
import React from 'react';
import * as d3 from 'd3';

function BarChart({ data}) {
  const ref = useD3(
    (svg) => {
      const width = svg.attr("width");
      const height = svg.attr("height");
      const xScale = d3.scaleLinear()
    		     .domain([0, width])
    			 .range([0, width]);
      const yScale = d3.scaleLinear()
              .domain([0, height])
            .range([0, height]);
      const h = height-height*0.4;    
      
      
      svg.selectAll("rect")
        .data(data)
        .enter()
        .append("rect")
        .attr("rx",15)
        .attr("x", (d, i) => xScale(i * 30))
        .attr("y", (d, i) =>yScale(( h - 7.5 * d[1]*100 - 50) ))
        .attr("width", 22.5)
        .attr("height", (d, i) => d[1] * 7.5 *100 )
        .attr("fill", "navy")
        .attr("class", "bar")
        .append("title")
        .text((d) => d);
        
    
      svg.selectAll('text.rotation')
      .data(data)
      .enter()
      .append('text')
      .text((d)=>d[0])
      .classed('rotation', true)
      .attr('fill', 'black')
      .attr('transform', (d,i)=>{
        return 'translate( '+ (xScale(i * 30) +4.5) +' , '+ (yScale(h-40)  ) +'), rotate(90)';
      })
      .style("font-size",height*0.01);
      

      
    },
    [data]
  );

  return (
    <svg
      ref={ref}
      style={{
        height: "100%",
        width: "100%",
        marginRight: "auto",
        marginLeft: "20%",
        textAlign: "center"
      }}
    >
      <g className="plot-area" />
      <g className="x-axis" />
      <g className="y-axis" />
    </svg>
  );
}

export default BarChart;
