import MusiaLogo from '../assets/logo_MusIA.png';
import Listening from './Listening';
import '../styles/Home.css';
import { useEffect, useState } from 'react';

function Home({ setIsContent, isListening, setIsListening, isReceiving, setIsReceiving }) {
  const [id, setid] = useState({});
  const [FinishL, setIsFinishL] = useState(false);

  useEffect(() => {
    let mediaRecorder = null;
    let chunks = [];

    const startRecording = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        mediaRecorder = new MediaRecorder(stream);
        mediaRecorder.ondataavailable = event => {
          chunks.push(event.data);
        };
        mediaRecorder.onstop = async () => {
          const blob = new Blob(chunks, { type: "audio/mp3" });
          const formData = new FormData();
          formData.append("audioBlob", blob);
          const response = await fetch("https://ee0c-185-144-25-51.eu.ngrok.io/api/v01/post/audio-blob/", {
            method: "POST",
            body: formData,
            headers: {
              "ngrok-skip-browser-warning": "69420",
            },
          });
          const id = await response.text();
          setid(id);
          setIsFinishL(true);
        };
        mediaRecorder.start(7000);
        setTimeout(() => {
          mediaRecorder.stop();
        }, 15000);
      } catch (error) {
        setIsReceiving(false);
        setIsListening(false);
        setIsContent(0);
      }
    };

    if (isListening && !FinishL) {
      startRecording();
    }

    if (FinishL) {
      setIsReceiving(true);
    }

    return () => {
      if (mediaRecorder && mediaRecorder.state !== "inactive") {
        mediaRecorder.stop();
      }
    };
  }, [isListening, FinishL, setIsListening, setIsReceiving, setIsContent, setid]);

  return isListening || isReceiving ? (
    <Listening FinishL={FinishL} id={id} setIsContent={setIsContent} isReceiving={isReceiving} setIsReceiving={setIsReceiving} setIsListening={setIsListening} />
  ) : (
    <div className="Home">
      <button className="transbutton" alt="Stop Listening" onClick={() => setIsListening(true)}>
        <img src={MusiaLogo} className="App-logo-2" alt="Logo" />
      </button>
      <p className="NameButton">Cliquez sur l'icone pour démarrer l'écoute.</p>
      <button className="Button" alt="Change to URL" onClick={() => setIsContent(1)}>
        Test URL
      </button>
    </div>
  );
}

export default Home;
