import '../styles/UrlContent.css';
import { useState, useEffect } from 'react'
import Prediction from './Prediction';

function Url({isContent,setIsContent,isReceiving,setIsReceiving}) {
  
    const [link, setlink] = useState("");
    const [isDisplay, setIsDisplay] = useState(false);
    const [Result, setResult] = useState(false);
    const [data, setdata] = useState({});

    const handleSubmit = (event) => {
        event.preventDefault();
        var xmlhttp = new XMLHttpRequest();
        var url = "http://127.0.0.1:5000/api/v01/post/URL/";
        xmlhttp.open("POST", url, true);
        xmlhttp.setRequestHeader("Content-type", "text");
        xmlhttp.onreadystatechange = function() {//Call a function when the state changes.
            if(xmlhttp.readyState == 4 && xmlhttp.status == 200) {
                setResult(true)
            }
          }
          
        xmlhttp.send(link);
    }

    useEffect(() => {
      if(isReceiving===true){
        fetch('http://localhost:5000/api/v01/get/resURL')
        .then(response => {
            console.log(response)
            return response.json(); // <<- Return the JSON Object
        })
        .then(result => {
          setdata(result);
          setIsDisplay(true);
        })
        .catch(error => { // Use .catch() to catch exceptions. Either in the request or any of your .then() blocks
            console.error(error); // Log the error object in the console.
            const errorMessage = 'Something went wrong';
            console.log(errorMessage);
        });
      }
  },[isReceiving,isDisplay]);

  return isReceiving ? (
    <Prediction isDisplay={isDisplay} setIsDisplay={setIsDisplay} data={data} />
  )  :  (
    <div className="UrlContent">
        
        <form className='Form' onSubmit={handleSubmit}>
      <label>Entrer un nom ou URL : 
        <input 
          className='inpute'
          type="text" 
          value={link}
          onChange={(e) => setlink(e.target.value)}
        />
      </label>
      <input type="submit" className='Button'/>
    </form>
    </div>
  );
}

export default Url;
