import '../styles/Content.css';
import { useState } from 'react'
import Listening from './Listening';
import Home from './Home';
import Url from './UrlComponent'


function Content({isContent,setIsContent}) {
  
  const [isReceiving, setIsReceiving] = useState(false)
  const [isListening, setIsListening] = useState(false)


  // LISTENING TO THE MICROPHONE
  function renderSwitch(param) {
    switch(param) {
      case 0:
        return <Home setIsContent={setIsContent} isListening={isListening} setIsListening={setIsListening} isReceiving={isReceiving} setIsReceiving={setIsReceiving}/>;
      case 1:
        return <Url isContent={isContent} setIsContent={setIsContent} isReceiving={isReceiving} setIsReceiving={setIsReceiving}/>;
      case 2:
        return <Listening setIsContent={setIsContent} isReceiving={isReceiving} setIsReceiving={setIsReceiving} setIsListening={setIsListening}/>;
      default:
        return  <div>
                </div>;
    }
  }

    

  return(
    <div className="Content">
      {renderSwitch(isContent)}
    </div>
  );
}

export default Content;
