import MusiaLogotransparent from '../assets/logo_trans.png'
import '../styles/Content.css';

import { useState, useEffect } from 'react'
import Prediction from './Prediction';

function Listening({setIsContent,isReceiving,setIsReceiving,setIsListening}) {
  
  const [data, setdata] = useState({});
  const [isDisplay, setIsDisplay] = useState(false);

  function back(){
    setIsContent(0)
    setIsListening(false)
    setIsReceiving(false)
  }
  useEffect(() => {
    if(isReceiving===true && isDisplay ===false){
      fetch('http://localhost:5000/api/v01/get/res')
      .then(response => {
          console.log(response)
          return response.json(); // <<- Return the JSON Object
      })
      .then(result => {
        console.log(result)
        if(result[1] === -1)
        {
          alert("La musique n'a pas été reconnu essayons par URL")
          setIsContent(1)
          setIsListening(false)
          setIsReceiving(false)
        }
        else{
          setdata(result);
          setIsDisplay(true);
        }
        
      })
      .catch(error => { // Use .catch() to catch exceptions. Either in the request or any of your .then() blocks
          console.error(error); // Log the error object in the console.
          const errorMessage = 'Something went wrong';
          console.log(errorMessage);
      });
    }
},[isReceiving,isDisplay,data]);
 
    
  
    


  return isReceiving ? (
    <Prediction isDisplay={isDisplay} setIsDisplay={setIsDisplay} data={data} />
  )  :  (
    <div className="ContentListening">
        <button className="transbutton" alt="Listening" onClick={() => back()}><img src={MusiaLogotransparent} className="App-logo" alt="Logo" /></button>
        <p>
          L'application est en écoute, veuillez patienter.
        </p>
    </div>
  );
} 

export default Listening;
