import '../styles/Content.css';
import { useState,useEffect } from 'react'
import Listening from './Listening';
import Home from './Home';
import Url from './UrlComponent'


function Content([Content,setIsContent]) {
  
  const [isReceiving, setIsReceiving] = useState(false)
  const [isListening, setIsListening] = useState(false)


  // LISTENING TO THE MICROPHONE
  function renderSwitch(param) {
    switch(param) {
      case 0:
        return <Home setIsContent={setIsContent} isListening={isListening} setIsListening={setIsListening} isReceiving={isReceiving} setIsReceiving={setIsReceiving}/>;
      case 1:
        return <Url isContent={isContent} setIsContent={setIsContent} isReceiving={isReceiving} setIsReceiving={setIsReceiving}/>;
      case 2:
        return <Listening setIsContent={setIsContent} isReceiving={isReceiving} setIsReceiving={setIsReceiving} setIsListening={setIsListening}/>;
      default:
        return  <div>
                </div>;
    }
  }
 
    
    useEffect(() => {
      if(isListening === true){
        var constraints = { audio: true }
      var chunks = []
      navigator.mediaDevices.getUserMedia(constraints)
      .then(function(stream) {
          const recorder = new MediaRecorder(stream);
          recorder.ondataavailable = event => {
              chunks.push(event.data);
              var blob = new Blob(chunks, {
                  type: "audio/wav"
              });
              var xmlhttp = new XMLHttpRequest();
              var url = "http://127.0.0.1:5000/api/v01/post/audio-blob/";
              xmlhttp.open("POST", url, true);
              xmlhttp.setRequestHeader("Content-type", "audio/wav");
              xmlhttp.send(blob);
          };
          
          const sleep = time => new Promise(resolve => setTimeout(resolve, time));
  
        (async () => {
          
          recorder.start(21000);
          await sleep(22000);
          await recorder.stop();
          setIsListening(false);
          setIsReceiving(true);
      })();
      })
      .catch(function(err) {
          /* handle the error */
      });

      }
      
  }, [isListening]);

    

  return(
    <div className="Content">
      {renderSwitch(isContent)}
    </div>
  );
}

export default Content;
