import '../styles/Menu.css';
import Select from 'react-select';

const options = [
  { value: 'sklearn', label: 'Sklearn' },
  { value: 'python', label: 'Python' },
  { value: 'C++', label: 'C++' },
];

function Menu({selectedModel,setSelectedModel}) {
  const handleChange = selectedModel => {
    setSelectedModel(selectedModel.value);
    if (selectedModel && selectedModel.value === 'C++') {
      alert('C++ is not available yet');
    }
  };
  const customStyles = {
    singleValue: (base) => ({ ...base, color: 'black' }),
    option: (styles) => ({ ...styles, color: 'black' }),
  };
  return (
    <div className="Menu">
      <Select
        className="Menu-select"
        classNamePrefix="Menu"
        options={options}
        value={selectedModel}
        onChange={handleChange}
        getOptionLabel={(option) => option.label}
        getOptionValue={(option) => option.value}
        isClearable={true}
        styles={customStyles}
      />
        <p>
          Cette application a été créée dans le but d'apprendre l'IA dans le cadre de notre projet de 4e année
        </p>
        <p>
          Le dataset est composé de 12 genres : Classical / Disco-Boogie / Electro / Hiphop-Rap / Jazz / Lofi / Medieval / Pop / R&B-Soul / Reggae / Retro / Rock
          Chaque genre possède environ 200 musiques
        </p>
      
    </div>
  );
}

export default Menu;
