import { useD3 } from '../hooks/useD3';
import React from 'react';
import * as d3 from 'd3';

function BarChart({ data ,name}) {
  const ref = useD3(
    (svg) => {
      const xScale = d3.scaleLinear()
    		     .domain([0, 500])
    			 .range([0, 500]);
    const yScale = d3.scaleLinear()
    		     .domain([0, 500])
    			 .range([0, 500]);
    const h = 300;    
    
      svg.selectAll("rect")
        .data(data)
        .enter()
        .append("rect")
        .attr("rx",10)
        .attr("x", (d, i) => xScale(i * 30))
        .attr("y", (d, i) =>yScale(( h - 3 * d[1]*100 - 50) ))
        .attr("width", 25)
        .attr("height", (d, i) => d[1] * 3 *100 )
        .attr("fill", "navy")
        .attr("class", "bar")
        .append("title")
        .text((d) => d);
        
    
      svg.selectAll('text.rotation')
      .data(data)
      .enter()
      .append('text')
      .text((d)=>d[0])
      .classed('rotation', true)
      .attr('fill', 'black')
      .attr('transform', (d,i)=>{
        return 'translate( '+ (xScale(i * 30) +15) +' , '+ (yScale(h-20)  + d[0].length*12 -15) +'), rotate(-90)';
      })
      .style("font-size",12);
      

      
    },
    [data]
  );

  return (
    <svg
      ref={ref}
      style={{
        height: 500,
        width: "100%",
        marginRight: "0px",
        marginLeft: "50px",
      }}
    >
      <g className="plot-area" />
      <g className="x-axis" />
      <g className="y-axis" />
    </svg>
  );
}

export default BarChart;
