import MusiaLogo from '../assets/logo_MusIA.png';
import Listening from './Listening';
import '../styles/Home.css';
import { useEffect, useState } from 'react';

function Home({ setIsContent, isListening, setIsListening, isReceiving, setIsReceiving }) {
  const [isFetching, setIsFetching] = useState(false);
  const [id, setId] = useState('');

  useEffect(() => {
    if (isListening && !isFetching) {
      setIsFetching(true);

      const constraints = { audio: true };
      const chunks = [];

      navigator.mediaDevices.getUserMedia(constraints)
        .then((stream) => {
          const recorder = new MediaRecorder(stream);

          recorder.ondataavailable = (event) => {
            chunks.push(event.data);

            const blob = new Blob(chunks, { type: 'audio/wav' });
            const formData = new FormData();

            formData.append('audio-blob', blob);

            const options = {
              method: 'POST',
              body: formData,
              headers: {
                'ngrok-skip-browser-warning': '69420',
              },
            };

            fetch('https://ee0c-185-144-25-51.eu.ngrok.io/api/v01/post/audio-blob/', options)
              .then((response) => response.text())
              .then((id) => {
                setIsFetching(false);
                setIsListening(false);
                setIsReceiving(true);
                setIsContent(0);
                setId(id); // Set the id state variable
                console.log(id);
              })
              .catch((error) => {
                setIsFetching(false);
                setIsReceiving(false);
                setIsListening(false);
                setIsContent(0);
                console.error(error);
              });
          };

          recorder.start(7000);
          setTimeout(() => {
            recorder.stop();
          }, 15000);
        })
        .catch((error) => {
          setIsFetching(false);
          setIsReceiving(false);
          setIsListening(false);
          setIsContent(0);
          console.error(error);
        });
    }
  }, [isListening, setIsContent, setIsFetching, setIsListening, setIsReceiving]);

  return isListening || isReceiving ? (
    <Listening setIsContent={setIsContent} isReceiving={isReceiving} setIsReceiving={setIsReceiving} id={id} /> // Pass the id prop
  ) : (
    <div className="Home">
      <button className="transbutton" alt="Stop Listening" onClick={() => setIsListening(true)}>
        <img src={MusiaLogo} className="App-logo-2" alt="Logo" />
      </button>
      <p className="NameButton">Cliquez sur l'icone pour démarrer l'écoute.</p>
      <button className="Button" alt="Change to URL" onClick={() => setIsContent(1)}>
        Test URL
      </button>
    </div>
  );
}

export default Home;
