import MusiaLogo from '../assets/logo_MusIA.png';
import Listening from './Listening';
import '../styles/Home.css';
import { useEffect, useState } from 'react';

function Home({ setIsContent, isListening, setIsListening, isReceiving, setIsReceiving }) {
  const [id, setId] = useState('');
  const [isFinishL, setIsFinishL] = useState(false);

  useEffect(() => {
    let chunks = [];
    let recorder;

    const handleDataAvailable = event => {
      chunks.push(event.data);
    };

    const handleStop = async () => {
      const blob = new Blob(chunks, {
        type: 'audio/mp3'
      });
      const formData = new FormData();
      formData.append('audio-blob', blob);
      try {
        const response = await fetch('https://ee0c-185-144-25-51.eu.ngrok.io/api/v01/post/audio-blob/', {
          method: 'POST',
          headers: {
            'ngrok-skip-browser-warning': '69420'
          },
          body: formData
        });
        const responseData = await response.json();
        setId(responseData);
        setIsListening(false);
      } catch (error) {
        console.error(error);
        setIsReceiving(false);
        setIsListening(false);
        setIsContent(0);
      }
    };

    const handleStart = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        recorder = new MediaRecorder(stream);
        recorder.addEventListener('dataavailable', handleDataAvailable);
        recorder.addEventListener('stop', handleStop);
        recorder.start(7000);
        setIsFinishL(true);
        setTimeout(() => {
          recorder.stop();
        }, 8000);
      } catch (error) {
        console.error(error);
        setIsReceiving(false);
        setIsListening(false);
        setIsContent(0);
      }
    };

    if (isListening && !isFinishL) {
      handleStart();
    }

    return () => {
      if (recorder) {
        recorder.removeEventListener('dataavailable', handleDataAvailable);
        recorder.removeEventListener('stop', handleStop);
      }
    };
  }, [isListening, isFinishL, setIsContent, setIsListening, setIsReceiving]);

  return isListening || isReceiving ? (
    <Listening FinishL={isFinishL} id={id} setIsContent={setIsContent} isReceiving={isReceiving} setIsReceiving={setIsReceiving} setIsListening={setIsListening} />
  ) : (
    <div className="Home">
      <button className="transbutton" alt="Stop Listening" onClick={() => setIsListening(true)}>
        <img src={MusiaLogo} className="App-logo-2" alt="Logo" />
      </button>
      <p className='NameButton'>
        Cliquez sur l'icone pour démarrer l'écoute.
      </p>
      <button className="Button" alt="Change to URL" onClick={() => setIsContent(1)}>Test URL</button>
    </div>
  );
}

export default Home;
