import MusiaLogo from '../assets/logo_MusIA.png';
import Listening from './Listening';
import '../styles/Home.css';
import { useEffect, useState } from 'react';

function Home({ setIsContent, isListening, setIsListening, isReceiving, setIsReceiving }) {
  const [id, setId] = useState(null);
  const [isFinishL, setIsFinishL] = useState(false);

  useEffect(() => {
    if (isListening && !isFinishL) {
      const constraints = { audio: true };
      const chunks = [];
      
      navigator.mediaDevices.getUserMedia(constraints)
        .then(function(stream) {
          const recorder = new MediaRecorder(stream);
          recorder.ondataavailable = event => {
            chunks.push(event.data);
            const blob = new Blob(chunks, {
              type: "audio/mp3"
            });

            const xhr = new XMLHttpRequest();
            const url = "https://ee0c-185-144-25-51.eu.ngrok.io/api/v01/post/audio-blob/";

            xhr.open("POST", url, true);
            xhr.setRequestHeader("Content-type", "audio/wav");
            xhr.setRequestHeader("ngrok-skip-browser-warning","69420")
            xhr.onreadystatechange = function() {
              if (xhr.readyState === 4 && xhr.status === 200) {
                setId(xhr.responseText);
                setIsListening(false);
              }
            }
            xhr.send(blob);
          };
          
          const sleep = time => new Promise(resolve => setTimeout(resolve, time));

          (async () => {
            if (isListening) {
              recorder.start(7000);
              await sleep(8000);
              await recorder.stop();
              setIsFinishL(true);
            }
          })();
        })
        .catch(function(err) {
          setIsReceiving(false);
          setIsListening(false);
          setIsContent(0);
        });
    }
    if (isFinishL) {
      setIsReceiving(true);
    }
  }, [isListening, isFinishL, setIsContent, setIsListening, setIsReceiving]);

  return isListening || isReceiving ? (
    <Listening id={id} setIsContent={setIsContent} setIsReceiving={setIsReceiving} setIsListening={setIsListening} />
  ) : (
    <div className="Home">
      <button className="transbutton" alt="Stop Listening" onClick={() => setIsListening(true)}>
        <img src={MusiaLogo} className="App-logo-2" alt="Logo" />
      </button>
      <p className='NameButton'>
        Cliquez sur l'icone pour démarrer l'écoute.
      </p>
      <button className="Button" alt="Change to URL" onClick={() => setIsContent(1)}>Test URL</button>
    </div>
  );
}

export default Home;
